class LevusVerticalScroll{constructor(t){this.id=document.querySelector(t),this.scroll=this.id.querySelector("ul"),this.elements=this.id.querySelectorAll("li"),this.up=this.id.querySelector(".up"),this.down=this.id.querySelector(".down")}clone(){this.elements.length<=14&&(this.elements.forEach(t=>{const s=t.cloneNode(!0);this.scroll.append(s)}),this.elements=this.scroll.querySelectorAll(".levus-vertical-scroll li"))}buttons(){this.up.style.display=this.down.style.display="block"}arrow(){this.up.addEventListener("click",()=>{const t=this.scroll.lastElementChild;this.scroll.prepend(t),this.scroll.style.transform="translateY(-60px)",this.scroll.style.transition="",setTimeout(()=>{this.scroll.style.transform="translateY(0)",this.scroll.style.transition="all .5s"},300)}),this.down.addEventListener("click",()=>{const t=this.scroll.firstElementChild;this.scroll.append(t),this.scroll.style.transform="translateY(60px)",this.scroll.style.transition="",setTimeout(()=>{this.scroll.style.transform="translateY(0)",this.scroll.style.transition="all .5s"},300)})}init(){this.clone(),this.buttons(),this.arrow()}}

document.querySelector("#scroll")&&new LevusVerticalScroll("#scroll").init();
